
#include "example.h"

#define JSTRING(S) v8::String::NewFromUtf8(v8::Isolate::GetCurrent(), S, v8::String::kInternalizedString)

////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
void __registerModule(v8::Handle<v8::Object> target)
{
	v8::Isolate* isolate = target->GetIsolate();
	target->Set(JSTRING("ModuleName"), JSTRING("Example Addon"));
}

NODE_MODULE(example, __registerModule);
